export const isIOS = () => {
  return /iPad|iPhone|iPod/.test(navigator.userAgent) && !window.MSStream;
};

export const getARUrl = (modelUrl) => {
  if (isIOS()) {
    // For iOS, use .reality file if available, fallback to .usdz
    const realityUrl = modelUrl.replace('.glb', '.reality');
    const usdzUrl = modelUrl.replace('.glb', '.usdz');

    // You can implement a check here to see if the .reality file exists
    // For now, we'll use .usdz as it's more commonly available
    return usdzUrl;
  }

  // For Android and other platforms, use the original GLB
  return modelUrl;
};
