import React, { Suspense } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, useGLTF } from '@react-three/drei';

function Model() {
  // Note: You'll need to replace this with your actual 3D model path
  const { scene } = useGLTF('/geodome.glb');
  return <primitive object={scene} />;
}

function ARViewer() {
  return (
    <section className="h-screen bg-gray-100" id="ar-experience">
      <div className="container mx-auto px-4 py-16">
        <h2 className="text-4xl font-bold text-center mb-8">
          Visualize Your Geodome
        </h2>
        <div className="h-[600px] rounded-lg overflow-hidden shadow-xl">
          <Canvas camera={{ position: [0, 2, 5] }}>
            <ambientLight intensity={0.5} />
            <directionalLight position={[10, 10, 5]} intensity={1} />
            <Suspense fallback={null}>
              <Model />
            </Suspense>
            <OrbitControls />
          </Canvas>
        </div>
        <div className="text-center mt-8">
          <button className="bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700 transition-colors">
            View in AR
          </button>
        </div>
      </div>
    </section>
  );
}

export default ARViewer;
