import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import ModelViewer from './ModelViewer';
import QRCode from 'qrcode.react';
import ARButton from './ARButton';

const domeOptions = [
  {
    id: 'dome1',
    name: 'Geodome Nordic',
    description: 'Design escandinavo com painéis de madeira e vidro, perfeito para integração com a natureza',
    size: '6m x 6m',
    modelFile: '/models/geodome-classic.usdz',
    modelFileWeb: '/models/ImageToStl.com_geodome_d15m_4v_woodframe.glb',
    images: ['/images/domes/snowdon-dome.jpg'],
    features: [
      'Painéis de madeira natural',
      'Janelas panorâmicas',
      'Porta frontal ampla',
      'Painel solar integrado',
      'Deck de entrada',
      'Dois andares'
    ]
  },
  {
    id: 'dome2',
    name: 'Geodome Urban',
    description: 'Versão moderna com acabamento premium em alumínio e design minimalista',
    size: '5m x 5m',
    modelFile: '/models/geodome-compact.usdz',
    modelFileWeb: '/models/zomepod.glb',
    images: ['/images/domes/dome-night.jpg'],
    features: [
      'Acabamento em alumínio',
      'Design geométrico moderno',
      'Isolamento térmico',
      'Porta de vidro temperado',
      'Iluminação LED integrada',
      'Ideal para home office'
    ]
  }
];

function DomeSection() {
  const [selectedDome, setSelectedDome] = useState(null);
  const [showQRCode, setShowQRCode] = useState(false);
  const [shareUrl, setShareUrl] = useState('');
  const [showAR, setShowAR] = useState([]);

  const closeViewer = () => {
    setSelectedDome(null);
    setShowQRCode(false);
    setShareUrl('');
    setShowAR([]);
  };

  const handleSaveLocation = async (dome) => {
    try {
      // First request location permission
      const permissionStatus = await navigator.permissions.query({ name: 'geolocation' });

      if (permissionStatus.state === 'denied') {
        throw new Error('Permissão de localização negada');
      }

      // Get position only after permission is granted
      const position = await new Promise((resolve, reject) => {
        navigator.geolocation.getCurrentPosition(resolve, reject, {
          enableHighAccuracy: true,
          timeout: 5000,
          maximumAge: 0
        });
      });

      // Get device orientation
      let deviceOrientation = null;
      if (typeof DeviceOrientationEvent.requestPermission === 'function') {
        const orientationPermission = await DeviceOrientationEvent.requestPermission();
        if (orientationPermission === 'granted') {
          deviceOrientation = await new Promise(resolve => {
            const handleOrientation = (event) => {
              window.removeEventListener('deviceorientation', handleOrientation);
              resolve({
                alpha: event.alpha,
                beta: event.beta,
                gamma: event.gamma
              });
            };
            window.addEventListener('deviceorientation', handleOrientation);
          });
        }
      }

      // Save anchor data
      const anchorData = {
        lat: position.coords.latitude,
        lng: position.coords.longitude,
        orientation: deviceOrientation,
        modelUrl: dome.modelFile,
        timestamp: new Date().toISOString()
      };

      const response = await fetch('/api/save-ar-anchor', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(anchorData),
      });

      if (!response.ok) {
        throw new Error('Erro ao salvar localização');
      }

      const { id } = await response.json();
      const newShareUrl = `${window.location.origin}/ar-view/${id}`;
      setShareUrl(newShareUrl);
      setShowQRCode(true);

    } catch (error) {
      console.error('Error saving location:', error);
      alert('Erro ao salvar localização. Por favor, permita acesso à localização.');
    }
  };

  return (
    <>
      <section className="py-16 bg-gray-900">
        <div className="container mx-auto px-4">
          <div className="text-center mb-12">
            <h2 className="text-3xl font-bold text-white mb-4">Nossos Modelos</h2>
            <p className="text-gray-400">Escolha o design perfeito para seu espaço</p>
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 max-w-7xl mx-auto">
            {domeOptions.map((dome) => (
              <motion.div
                key={dome.id}
                className="bg-gray-800 rounded-xl overflow-hidden shadow-2xl"
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
              >
                <div className="relative aspect-[4/3] overflow-hidden">
                  <img
                    src={dome.images[0]}
                    alt={dome.name}
                    className="w-full h-full object-cover transition-transform duration-500 hover:scale-105"
                  />
                  <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-gray-900 via-gray-900/60 to-transparent p-6">
                    <h3 className="text-2xl font-bold text-white mb-2">{dome.name}</h3>
                    <p className="text-gray-300 text-lg">{dome.size}</p>
                  </div>
                </div>

                <div className="p-6">
                  <p className="text-gray-300 mb-6 text-lg">{dome.description}</p>

                  <div className="mb-8">
                    <h4 className="text-white text-lg font-semibold mb-4">Características:</h4>
                    <ul className="grid grid-cols-2 gap-3">
                      {dome.features.map((feature, idx) => (
                        <li key={idx} className="text-gray-400 flex items-center text-sm">
                          <svg className="w-5 h-5 mr-2 text-emerald-500 flex-shrink-0" fill="currentColor" viewBox="0 0 20 20">
                            <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd"/>
                          </svg>
                          {feature}
                        </li>
                      ))}
                    </ul>
                  </div>

                  <div className="flex gap-4">
                    <a
                      href={`ar-handler.html?model=${encodeURIComponent(dome.modelFile)}`}
                      rel="ar"
                      className="flex-1 bg-emerald-600 hover:bg-emerald-700 text-white py-3 px-4 rounded-lg text-center font-medium transition-colors flex items-center justify-center"
                    >
                      <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                      </svg>
                      Ver em AR
                    </a>
                    <button
                      onClick={() => setSelectedDome(dome)}
                      className="flex-1 bg-gray-700 hover:bg-gray-600 text-white py-3 px-4 rounded-lg text-center font-medium transition-colors flex items-center justify-center"
                    >
                      <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
                      </svg>
                      Ver em 3D
                    </button>
                    <button
                      onClick={() => {
                        setShowAR((prevShowAR) => [...prevShowAR, dome.id]);
                      }}
                      className="flex-1 bg-emerald-600 hover:bg-emerald-700 text-white py-3 px-4 rounded-lg text-center font-medium transition-colors flex items-center justify-center"
                    >
                      <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 18h.01M8 21h8a2 2 0 002-2V5a2 2 0 00-2-2H8a2 2 0 00-2 2v14a2 2 0 002 2z" />
                      </svg>
                      Ver em RA
                    </button>
                  </div>
                  {showAR.includes(dome.id) && <ARButton modelUrl={dome.modelFile} />}
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      <AnimatePresence>
        {selectedDome && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-75"
          >
            <div className="relative w-full h-full">
              <button
                onClick={closeViewer}
                className="absolute top-4 right-4 z-10 p-2 bg-gray-800 rounded-full text-white hover:bg-gray-700 transition-colors"
              >
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
              <ModelViewer modelUrl={selectedDome.modelFileWeb} />

              {/* AR Location Saving UI */}
              {window.location.href.includes('ar=true') && !showQRCode && (
                <button
                  onClick={() => handleSaveLocation(selectedDome)}
                  className="absolute bottom-4 right-4 z-10 bg-emerald-600 text-white px-6 py-3 rounded-lg shadow-lg hover:bg-emerald-700 transition-colors flex items-center space-x-2"
                >
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                  </svg>
                  <span>Salvar Localização</span>
                </button>
              )}

              {/* QR Code Display */}
              {showQRCode && (
                <div className="absolute top-4 left-4 z-10 bg-white p-4 rounded-lg shadow-lg">
                  <QRCode value={shareUrl} size={128} />
                  <p className="mt-2 text-sm text-center text-gray-700">
                    Escaneie para ver neste local
                  </p>
                  <button
                    onClick={() => setShowQRCode(false)}
                    className="mt-2 w-full bg-gray-200 text-gray-700 px-4 py-2 rounded hover:bg-gray-300 transition-colors"
                  >
                    Fechar
                  </button>
                </div>
              )}
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </>
  );
}

export default DomeSection;
