import React, { useEffect, useState } from 'react';
import { useParams, useSearchParams } from 'react-router-dom';
import { getARUrl } from '../utils/arUtils';

export default function ARViewer() {
  const { modelId } = useParams();
  const [searchParams] = useSearchParams();
  const modelUrl = searchParams.get('model');
  const [error, setError] = useState(null);

  useEffect(() => {
    async function loadAR() {
      if (!modelUrl) return;

      try {
        setError(null);
        // Convert and get USDZ file URL
        const usdzUrl = await getARUrl(modelUrl);

        // Create AR Quick Look URL
        const arUrl = `${window.location.origin}/ar-handler.html?model=${encodeURIComponent(usdzUrl)}`;
        window.location.href = arUrl;
      } catch (err) {
        setError(err.message);
      }
    }

    loadAR();
  }, [modelUrl]);

  if (error) {
    return (
      <div className="w-full h-screen bg-gray-100 flex items-center justify-center">
        <div className="text-center text-red-600">
          <p>Error: {error}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="w-full h-screen bg-gray-100 flex items-center justify-center">
      <div className="text-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-emerald-600 mx-auto mb-4"></div>
        <p className="text-gray-600">Preparando visualizador AR...</p>
      </div>
    </div>
  );
}
