import React from 'react';
import { useParams, useSearchParams } from 'react-router-dom';
import { getARUrl } from '../utils/arUtils';

export default function ARViewer() {
  const { modelId } = useParams();
  const [searchParams] = useSearchParams();
  const modelUrl = searchParams.get('model');

  React.useEffect(() => {
    if (!modelUrl) return;

    // Get the GLB URL and redirect to AR handler
    const arUrl = getARUrl(modelUrl);
    const handlerUrl = `${window.location.origin}/ar-handler.html?model=${encodeURIComponent(arUrl)}`;
    window.location.href = handlerUrl;
  }, [modelUrl]);

  return (
    <div className="w-full h-screen bg-gray-100 flex items-center justify-center">
      <div className="text-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-emerald-600 mx-auto mb-4"></div>
        <p className="text-gray-600">Preparando visualizador AR...</p>
      </div>
    </div>
  );
}
