import React, { useState } from 'react';
import { motion } from 'framer-motion';
import ModelViewer from './ModelViewer';

const domeOptions = [
  {
    id: 'dome1',
    name: 'ZomePod',
    description: 'Design moderno e sustentável, perfeito para espaços de trabalho e lazer',
    size: '6m x 6m',
    modelFileWeb: '/models/zomepod.glb',
    image: '/images/domes/snowdon-dome.jpg',
    features: [
      'Estrutura modular',
      'Design contemporâneo',
      'Iluminação natural',
      'Ventilação cruzada',
      'Isolamento térmico',
      'Instalação rápida'
    ],
    specifications: {
      'Área': '28m²',
      'Altura': '3.5m',
      'Material': 'Madeira e Vidro',
      'Tempo de Montagem': '2-3 dias',
      'Garantia': '5 anos',
      'Capacidade': '4-6 pessoas'
    }
  },
  {
    id: 'dome2',
    name: 'Geodésica Noturna',
    description: 'Uma experiência única de hospedagem com vista panorâmica para as estrelas',
    size: '5m x 5m',
    modelFileWeb: '/models/zomepod.glb', // Using same model for demo
    image: '/images/domes/geosota-zome-lapt-1005x1024.jpg',
    features: [
      'Teto panorâmico',
      'Iluminação ambiente',
      'Isolamento térmico',
      'Ventilação natural',
      'Design geodésico',
      'Montagem rápida'
    ],
    specifications: {
      'Área': '20m²',
      'Altura': '3.2m',
      'Material': 'Alumínio e Vidro',
      'Tempo de Montagem': '1-2 dias',
      'Garantia': '3 anos',
      'Capacidade': '2-3 pessoas'
    }
  },
  {
    id: 'dome3',
    name: 'Cabana Cristal',
    description: 'Cabana moderna com design geométrico e amplas janelas para integração com a natureza',
    size: '4m x 6m',
    modelFileWeb: '/models/zomepod.glb', // Using same model for demo
    image: '/images/domes/nordic-dome.jpg',
    features: [
      'Painéis de vidro',
      'Estrutura geométrica',
      'Design minimalista',
      'Aquecimento integrado',
      'Deck externo',
      'Mobília inclusa'
    ],
    specifications: {
      'Área': '24m²',
      'Altura': '3.8m',
      'Material': 'Aço e Vidro Temperado',
      'Tempo de Montagem': '2-3 dias',
      'Garantia': '4 anos',
      'Capacidade': '2-4 pessoas'
    }
  }
];

function DomeSection() {
  const [selectedDomeId, setSelectedDomeId] = useState('dome1');
  const [show3DModel, setShow3DModel] = useState(false);

  const selectedDome = domeOptions.find(dome => dome.id === selectedDomeId);

  const handle3DClick = () => {
    setShow3DModel(!show3DModel);
  };

  const handleARClick = () => {
    const modelUrl = `${window.location.origin}${selectedDome.modelFileWeb}`;

    // For iOS, use USDZ file
    if (/iPhone|iPad|iPod/.test(navigator.userAgent)) {
      const usdzUrl = modelUrl.replace(/\.glb$/, '.usdz');
      window.location.href = usdzUrl;
      return;
    }

    // For other devices, use scene-viewer
    window.location.href = `intent://arvr.google.com/scene-viewer/1.0?file=${modelUrl}#Intent;scheme=https;package=com.google.android.googlequicksearchbox;action=android.intent.action.VIEW;S.browser_fallback_url=https://developers.google.com/ar;end;`;
  };

  return (
    <div className="min-h-screen bg-gray-50 py-12">
      <div className="container mx-auto px-4">
        <div className="text-center mb-12">
          <h2 className="text-4xl font-bold text-gray-900 mb-4">
            Nossos Modelos
          </h2>
          <p className="text-xl text-gray-600">
            Explore nossos modelos em 3D e Realidade Aumentada
          </p>
        </div>

        {/* Dome Selection Tabs */}
        <div className="flex justify-center mb-8">
          <div className="inline-flex rounded-lg border border-gray-200 bg-white p-1">
            {domeOptions.map((dome) => (
              <button
                key={dome.id}
                onClick={() => {
                  setSelectedDomeId(dome.id);
                  setShow3DModel(false);
                }}
                className={`px-4 py-2 rounded-md ${
                  selectedDomeId === dome.id
                    ? 'bg-emerald-600 text-white'
                    : 'text-gray-500 hover:text-gray-700'
                }`}
              >
                {dome.name}
              </button>
            ))}
          </div>
        </div>

        <div className="max-w-6xl mx-auto">
          <motion.div
            key={selectedDomeId}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            className="bg-white rounded-xl shadow-lg overflow-hidden"
          >
            <div className="grid grid-cols-1 md:grid-cols-2 gap-8 p-8">
              <div className="relative aspect-square bg-gray-100 rounded-lg overflow-hidden">
                {show3DModel ? (
                  <ModelViewer modelUrl={`${window.location.origin}${selectedDome.modelFileWeb}`} />
                ) : (
                  <img
                    src={selectedDome.image}
                    alt={selectedDome.name}
                    className="w-full h-full object-cover"
                  />
                )}
                <div className="absolute bottom-4 left-4 flex gap-2">
                  <button
                    onClick={handle3DClick}
                    className="bg-gray-800 hover:bg-gray-900 text-white py-2 px-4 rounded-lg text-center font-medium transition-colors flex items-center justify-center"
                  >
                    <svg className="h-5 w-5 mr-2" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M14 10l-2 1m0 0l-2-1m2 1v2.5M20 7l-2 1m2-1l-2-1m2 1v2.5M14 4l-2-1-2 1M4 7l2-1M4 7l2 1M4 7v2.5M12 21l-2-1m2 1l2-1m-2 1v-2.5M6 18l-2-1v-2.5M18 18l2-1v-2.5" />
                    </svg>
                    {show3DModel ? 'Ver Foto' : 'Ver em 3D'}
                  </button>
                  <button
                    onClick={handleARClick}
                    className="bg-emerald-600 hover:bg-emerald-700 text-white py-2 px-4 rounded-lg text-center font-medium transition-colors flex items-center justify-center"
                  >
                    <svg className="h-5 w-5 mr-2" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 18h.01M8 21h8a2 2 0 002-2V5a2 2 0 00-2-2H8a2 2 0 00-2 2v14a2 2 0 002 2z" />
                    </svg>
                    Ver em AR
                  </button>
                </div>
              </div>

              <div>
                <h3 className="text-2xl font-bold text-gray-900 mb-4">
                  {selectedDome.name}
                </h3>
                <p className="text-gray-600 mb-6">
                  {selectedDome.description}
                </p>

                <div className="space-y-6">
                  <div>
                    <h4 className="text-lg font-semibold text-gray-900 mb-3">
                      Características
                    </h4>
                    <ul className="grid grid-cols-2 gap-2">
                      {selectedDome.features.map((feature, index) => (
                        <li key={index} className="flex items-center text-gray-600">
                          <span className="mr-2">•</span>
                          {feature}
                        </li>
                      ))}
                    </ul>
                  </div>

                  <div>
                    <h4 className="text-lg font-semibold text-gray-900 mb-3">
                      Especificações
                    </h4>
                    <dl className="grid grid-cols-2 gap-2">
                      {Object.entries(selectedDome.specifications).map(([key, value]) => (
                        <div key={key}>
                          <dt className="text-gray-500">{key}</dt>
                          <dd className="text-gray-900 font-medium">{value}</dd>
                        </div>
                      ))}
                    </dl>
                  </div>
                </div>
              </div>
            </div>
          </motion.div>
        </div>
      </div>
    </div>
  );
}

export default DomeSection;
