import React from 'react';
import { useNavigate, useParams, useSearchParams } from 'react-router-dom';

export default function ARViewer() {
  const navigate = useNavigate();
  const { modelId } = useParams();
  const [searchParams] = useSearchParams();
  const modelUrl = searchParams.get('model');
  const iosUrl = modelUrl?.replace('.glb', '.usdz');

  if (!modelUrl) {
    return <div>No model URL provided</div>;
  }

  return (
    <div className="w-full h-screen bg-gray-100">
      <model-viewer
        src={modelUrl}
        ios-src={iosUrl}
        ar
        ar-modes="webxr scene-viewer quick-look"
        camera-controls
        auto-rotate
        style={{ width: '100%', height: '100%' }}
      >
        <button
          slot="ar-button"
          className="absolute bottom-4 left-4 right-4 bg-emerald-600 hover:bg-emerald-700 text-white py-3 px-4 rounded-lg text-center font-medium transition-colors flex items-center justify-center"
        >
          <svg className="h-5 w-5 mr-2" viewBox="0 0 24 24" fill="none" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 18h.01M8 21h8a2 2 0 002-2V5a2 2 0 00-2-2H8a2 2 0 00-2 2v14a2 2 0 002 2z" />
          </svg>
          Ver em RA
        </button>
      </model-viewer>
    </div>
  );
}
