import React, { useRef, useEffect, useState } from 'react';
import { useNavigate, useParams, useSearchParams } from 'react-router-dom';

export default function ARViewer() {
  const navigate = useNavigate();
  const { modelId } = useParams();
  const [searchParams] = useSearchParams();
  const modelUrl = searchParams.get('model');
  const modelViewerRef = useRef(null);
  const fileInputRef = useRef(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const modelViewer = modelViewerRef.current;
    let arStatusListener, loadListener, errorListener;

    if (modelViewer) {
      setIsLoading(true);

      arStatusListener = (event) => {
        console.log('AR Status:', event.detail);
      };

      loadListener = () => {
        console.log('Model loaded successfully');
        setIsLoading(false);
      };

      errorListener = (error) => {
        console.error('Model loading error:', error);
        setIsLoading(false);
      };

      modelViewer.addEventListener('ar-status', arStatusListener);
      modelViewer.addEventListener('load', loadListener);
      modelViewer.addEventListener('error', errorListener);

      // Check if running on iOS
      const isIOS = /iPad|iPhone|iPod/.test(navigator.userAgent) && !window.MSStream;
      if (isIOS) {
        // Convert GLB to USDZ for iOS
        const usdzUrl = modelUrl.replace('.glb', '.usdz');
        modelViewer.setAttribute('ios-src', usdzUrl);
        modelViewer.setAttribute('ar-modes', 'quick-look');
      } else {
        modelViewer.setAttribute('ar-modes', 'webxr scene-viewer');
      }
    }

    return () => {
      if (modelViewer) {
        modelViewer.removeEventListener('ar-status', arStatusListener);
        modelViewer.removeEventListener('load', loadListener);
        modelViewer.removeEventListener('error', errorListener);
      }
    };
  }, [modelUrl]);

  if (!modelUrl) {
    return (
      <div className="fixed inset-0 bg-black bg-opacity-90 z-50 flex items-center justify-center text-white">
        <div className="text-center">
          <h2 className="text-2xl font-bold mb-4">Modelo nÃ£o encontrado</h2>
          <button
            onClick={() => navigate('/')}
            className="bg-emerald-600 hover:bg-emerald-700 text-white py-2 px-4 rounded-lg font-medium transition-colors"
          >
            Voltar
          </button>
        </div>
      </div>
    );
  }

  const handleSavePosition = () => {
    if (!modelViewerRef.current) return;

    const camera = modelViewerRef.current.getCameraOrbit();
    const scale = modelViewerRef.current.scale;

    const sessionData = {
      camera: {
        orbit: camera,
        scale: scale
      },
      modelUrl: modelUrl,
      timestamp: Date.now()
    };

    const blob = new Blob([JSON.stringify(sessionData, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `ar-session-${modelId || 'model'}.json`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const handleLoadPosition = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const sessionData = JSON.parse(e.target.result);
        if (modelViewerRef.current && sessionData.camera) {
          modelViewerRef.current.cameraOrbit = sessionData.camera.orbit;
          modelViewerRef.current.scale = sessionData.camera.scale;
        }
      } catch (error) {
        console.error('Error loading position:', error);
      }
    };
    reader.readAsText(file);
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-90 z-50">
      <model-viewer
        ref={modelViewerRef}
        src={modelUrl}
        ar
        camera-controls
        auto-rotate
        interaction-prompt="auto"
        ar-placement="floor"
        shadow-intensity="1"
        environment-image="neutral"
        exposure="1"
        style={{
          width: '100%',
          height: '100%',
          backgroundColor: '#1a1a1a'
        }}
        ar-scale="fixed"
        loading="eager"
        reveal="auto"
        camera-orbit="45deg 55deg 2.5m"
        field-of-view="30deg"
        min-camera-orbit="auto auto 2m"
        max-camera-orbit="auto auto 10m"
        min-field-of-view="10deg"
        max-field-of-view="45deg"
      >
        {isLoading && (
          <div slot="poster" className="flex items-center justify-center h-full">
            <div className="animate-spin rounded-full h-32 w-32 border-t-2 border-b-2 border-emerald-500"></div>
          </div>
        )}
        <button
          slot="ar-button"
          className="absolute bottom-4 right-4 bg-emerald-600 hover:bg-emerald-700 text-white py-3 px-6 rounded-lg font-medium transition-colors"
          style={{
            backgroundColor: '#059669',
            border: 'none',
            borderRadius: '8px',
            color: 'white',
            fontFamily: 'Inter, sans-serif',
            fontSize: '16px',
            padding: '12px 24px',
            cursor: 'pointer',
            display: 'flex',
            alignItems: 'center',
            gap: '8px'
          }}
        >
          <span role="img" aria-label="wave">ðŸ‘‹</span>
          Ver em AR
        </button>
      </model-viewer>

      <div className="absolute top-4 right-4 flex gap-2">
        <button
          onClick={() => fileInputRef.current?.click()}
          className="bg-white hover:bg-gray-100 text-gray-900 py-2 px-4 rounded-lg font-medium transition-colors"
        >
          Carregar PosiÃ§Ã£o
        </button>
        <button
          onClick={handleSavePosition}
          className="bg-emerald-600 hover:bg-emerald-700 text-white py-2 px-4 rounded-lg font-medium transition-colors"
        >
          Salvar PosiÃ§Ã£o
        </button>
        <button
          onClick={() => navigate('/')}
          className="bg-gray-800 hover:bg-gray-900 text-white py-2 px-4 rounded-lg font-medium transition-colors"
        >
          âœ• Fechar
        </button>
      </div>

      <input
        ref={fileInputRef}
        type="file"
        accept=".json"
        onChange={handleLoadPosition}
        className="hidden"
      />
    </div>
  );
}
