export const getARUrl = async (modelUrl) => {
  try {
    // First check if .reality file already exists
    const realityUrl = modelUrl.replace('.glb', '.reality');
    const response = await fetch(realityUrl, { method: 'HEAD' });

    if (response.ok) {
      return realityUrl;
    }

    // If .reality file doesn't exist, convert it
    const convertResponse = await fetch('/api/convert-to-reality', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ glbPath: modelUrl }),
    });

    if (!convertResponse.ok) {
      throw new Error('Failed to convert file');
    }

    const { realityPath } = await convertResponse.json();
    return realityPath;
  } catch (error) {
    console.error('Error getting AR URL:', error);
    throw error;
  }
};
