import React, { Suspense } from 'react';
import { Canvas } from '@react-three/fiber';
import { Environment, OrbitControls, useGLTF } from '@react-three/drei';

function Model() {
  const { scene } = useGLTF('/models/zomepod.glb');

  return (
    <primitive 
      object={scene} 
      scale={1} 
      position={[0, 0, 0]}
      castShadow
      receiveShadow
    />
  );
}

const ModelViewer = () => {
  return (
    <div style={{ width: '100vw', height: '100vh', background: '#f0f0f0' }}>
      <Canvas 
        camera={{ position: [8, 5, 8], fov: 45 }}
        shadows
      >
        <ambientLight intensity={0.5} />
        <spotLight 
          position={[10, 10, 10]} 
          angle={0.15} 
          penumbra={1} 
          intensity={1}
          castShadow
        />
        <Suspense fallback={null}>
          <Model />
          <Environment preset="forest" background blur={0.6} />
        </Suspense>
        <OrbitControls 
          enablePan={true}
          enableZoom={true}
          enableRotate={true}
          minDistance={5}
          maxDistance={20}
        />
        <gridHelper args={[20, 20]} />
      </Canvas>
    </div>
  );
};

// Pre-load the model
useGLTF.preload('/models/zomepod.glb');

export default ModelViewer;
