import React from 'react';
import { motion } from 'framer-motion';

function Hero() {
  return (
    <section className="relative h-screen flex items-center">
      <div className="absolute inset-0 z-0">
        <img
          src="/images/geodome-hero.jpg"
          alt="Geodome in nature"
          className="w-full h-full object-cover"
        />
        <div className="absolute inset-0 bg-black bg-opacity-50"></div>
      </div>

      <div className="container mx-auto px-4 z-10 text-white">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
        >
          <h1 className="text-6xl font-bold mb-6">
            Geodome Flat Pack
          </h1>
          <p className="text-xl mb-8 max-w-2xl">
            A união perfeita entre estilo escandinavo, montagem simples e conforto premium.
            Seu refúgio na natureza com design minimalista.
          </p>
          <button className="bg-white text-black px-8 py-4 rounded-lg text-lg font-semibold hover:bg-gray-100 transition-colors">
            Explore Agora
          </button>
        </motion.div>
      </div>
    </section>
  );
}

export default Hero;
