import React, { Suspense, useState, useEffect } from 'react';
import { Canvas } from '@react-three/fiber';
import { Environment, OrbitControls, useGLTF, Plane } from '@react-three/drei';
import * as THREE from 'three';

function DetailedGround() {
  return (
    <group>
      {/* Main grass lawn - matching the image's well-maintained look */}
      <Plane
        receiveShadow
        rotation={[-Math.PI / 2, 0, 0]}
        position={[0, -0.1, 0]}
        args={[100, 100]}
      >
        <meshStandardMaterial
          color="#4CAF50"  // Brighter, more maintained lawn color
          roughness={0.8}
          metalness={0.1}
        />
      </Plane>

      {/* Subtle grass detail patches */}
      {[...Array(50)].map((_, i) => {
        const x = Math.random() * 30 - 15;
        const z = Math.random() * 30 - 15;
        const scale = 0.3 + Math.random() * 0.3;
        return (
          <mesh
            key={i}
            position={[x, -0.05, z]}
            rotation={[0, Math.random() * Math.PI, 0]}
            scale={[scale, scale, scale]}
          >
            <planeGeometry args={[0.3, 0.5]} />
            <meshStandardMaterial 
              color="#388E3C"
              side={THREE.DoubleSide}
              transparent
              opacity={0.7}
            />
          </mesh>
        );
      })}
    </group>
  );
}

function BackgroundTrees() {
  return (
    <group>
      {/* Scattered background trees, similar to the image */}
      {[...Array(12)].map((_, i) => {
        const angle = (i / 12) * Math.PI * 2;
        const radius = 25 + Math.random() * 5;
        return (
          <group key={i} position={[
            Math.cos(angle) * radius,
            0,
            Math.sin(angle) * radius
          ]}>
            {/* Tree trunk */}
            <mesh castShadow>
              <cylinderGeometry args={[0.3, 0.4, 5, 8]} />
              <meshStandardMaterial color="#5D4037" roughness={0.9} />
            </mesh>
            {/* Tree foliage */}
            <mesh position={[0, 4, 0]} castShadow>
              <sphereGeometry args={[2, 16, 16]} />
              <meshStandardMaterial color="#2E7D32" roughness={0.8} />
            </mesh>
          </group>
        );
      })}
    </group>
  );
}

function Model() {
  const { scene } = useGLTF('/models/zomepod.glb');

  return (
    <primitive 
      object={scene} 
      scale={1} 
      position={[0, 0, 0]}
      castShadow
      receiveShadow
    />
  );
}

const ModelViewer = () => {
  const [scanning, setScanning] = useState(false);
  const [customModel, setCustomModel] = useState(null);

  useEffect(() => {
    // Initialize Polycam
    const script = document.createElement('script');
    script.src = 'https://web.polycam.dev/js/web-sdk.js';
    script.async = true;
    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script);
    };
  }, []);

  const startScanning = async () => {
    try {
      setScanning(true);

      // Initialize Polycam capture
      const polycam = window.Polycam;

      if (!polycam) {
        throw new Error('Polycam SDK not loaded');
      }

      // Configure Polycam
      polycam.configure({
        apiKey: process.env.REACT_APP_POLYCAM_API_KEY,
        onCaptureComplete: async (result) => {
          try {
            // Download the 3D model
            const response = await fetch(result.modelUrl);
            const modelData = await response.blob();

            // Create object URL for the model
            const modelUrl = URL.createObjectURL(modelData);
            setCustomModel(modelUrl);

          } catch (error) {
            console.error('Error downloading model:', error);
            alert('Failed to download 3D model. Please try again.');
          }
        },
        onError: (error) => {
          console.error('Polycam error:', error);
          alert('An error occurred during scanning. Please try again.');
          setScanning(false);
        }
      });

      // Start the capture process
      await polycam.startCapture({
        mode: 'object', // for small-medium sized objects
        quality: 'high',
        guidanceMode: 'enhanced', // provides better user guidance
        maxDuration: 60, // 60 seconds max capture time
      });

    } catch (error) {
      console.error('Error starting scan:', error);
      alert('Failed to start 3D scanning. Please try again.');
      setScanning(false);
    }
  };

  function CustomModel({ url }) {
    const { scene } = useGLTF(url);
    return (
      <primitive 
        object={scene} 
        scale={1} 
        position={[0, 0, 0]}
        castShadow
        receiveShadow
      />
    );
  }

  return (
    <div style={{ width: '100vw', height: '100vh', background: '#87CEEB', position: 'relative' }}>
      <Canvas 
        camera={{ position: [15, 8, 15], fov: 45 }}
        shadows
        gl={{ antialias: true }}
        dpr={[1, 2]}
      >
        <color attach="background" args={['#B3E5FC']} />
        <fog attach="fog" args={['#B3E5FC', 40, 100]} />

        <ambientLight intensity={1.2} />
        <directionalLight
          position={[5, 10, 5]}
          intensity={2}
          castShadow
          shadow-mapSize-width={2048}
          shadow-mapSize-height={2048}
          shadow-camera-far={50}
          shadow-camera-left={-20}
          shadow-camera-right={20}
          shadow-camera-top={20}
          shadow-camera-bottom={-20}
        />

        <Suspense fallback={null}>
          {customModel ? (
            <CustomModel url={customModel} />
          ) : (
            <Model />
          )}
          <DetailedGround />
          <BackgroundTrees />
          <Environment preset="sunset" background={false} />
        </Suspense>

        <OrbitControls 
          enablePan={true}
          enableZoom={true}
          enableRotate={true}
          minDistance={5}
          maxDistance={30}
          maxPolarAngle={Math.PI / 2.1}
          target={[0, 2, 0]}
        />
      </Canvas>

      <button
        onClick={startScanning}
        disabled={scanning}
        style={{
          position: 'absolute',
          bottom: '20px',
          right: '20px',
          padding: '12px 24px',
          backgroundColor: scanning ? '#ccc' : '#007AFF',
          color: 'white',
          border: 'none',
          borderRadius: '8px',
          fontSize: '16px',
          fontWeight: 'bold',
          cursor: scanning ? 'default' : 'pointer',
          boxShadow: '0 2px 4px rgba(0,0,0,0.2)',
          display: 'flex',
          alignItems: 'center',
          gap: '8px'
        }}
      >
        {scanning ? (
          <>
            <span className="spinner"></span>
            Scanning...
          </>
        ) : (
          <>
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <circle cx="12" cy="12" r="3"/>
              <path d="M3 9h4M21 9h-4M3 15h4M21 15h-4"/>
            </svg>
            Scan 3D Object
          </>
        )}
      </button>

      <style>
        {`
          .spinner {
            width: 20px;
            height: 20px;
            border: 2px solid #ffffff;
            border-top: 2px solid transparent;
            border-radius: 50%;
            animation: spin 1s linear infinite;
          }

          @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
          }
        `}
      </style>
    </div>
  );
};

export default ModelViewer;
