import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import DomeOptions from './DomeOptions';
import { Link } from 'react-router-dom';

function Hero() {
  const [selectedDome, setSelectedDome] = useState(null);
  const [isMinimized, setIsMinimized] = useState(false);

  const handleDomeSelect = (dome) => {
    setSelectedDome(dome);
  };

  return (
    <section className="relative min-h-screen flex items-center overflow-hidden py-20">
      <div className="absolute inset-0 z-0">
        {!selectedDome ? (
          <>
            <img
              src="/images/hero/hero-main.jpg"
              alt="Geodome in nature"
              className="w-full h-full object-cover"
              onError={(e) => {
                e.target.onerror = null;
                e.target.src = 'https://via.placeholder.com/1920x1080?text=Geodome+Flat+Pack';
              }}
            />
            <div className="absolute inset-0 bg-gradient-to-r from-black/70 to-black/50"></div>
          </>
        ) : (
          <div className="w-full h-full">
            <div className="sketchfab-embed-wrapper w-full h-full">
              <iframe
                title={`${selectedDome.name} 3D View`}
                className="w-full h-full"
                frameBorder="0"
                allowFullScreen
                mozallowfullscreen="true"
                webkitallowfullscreen="true"
                allow="autoplay; fullscreen; xr-spatial-tracking"
                xr-spatial-tracking="true"
                execution-while-out-of-viewport="true"
                execution-while-not-rendered="true"
                web-share="true"
                src={`https://2f86-2804-140-8001-b99-a0e2-ec54-d26a-5e67.ngrok-free.app/ar-experience?model=${selectedDome.id}`}
              />
            </div>
          </div>
        )}
      </div>

      <AnimatePresence>
        {(!selectedDome || !isMinimized) && (
          <motion.div
            initial={{ opacity: 0, y: selectedDome ? 20 : 0 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 20 }}
            className="relative z-10 container mx-auto px-4"
          >
            <div className="text-center mb-12">
              <motion.h1
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                className="text-5xl md:text-6xl font-bold text-white mb-6"
              >
                Geodome Flat Pack
              </motion.h1>
              <motion.p
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.1 }}
                className="text-xl text-white/90 max-w-2xl mx-auto"
              >
                Design escandinavo, montagem simples e conforto premium
              </motion.p>
            </div>

            <DomeOptions onSelectDome={handleDomeSelect} />
            <div className="flex flex-col sm:flex-row gap-4 mt-8">
              <Link
                to="/3d-view"
                className="inline-block rounded-lg bg-indigo-600 px-4 py-1.5 text-base font-semibold leading-7 text-white shadow-sm ring-1 ring-indigo-600 hover:bg-indigo-700 hover:ring-indigo-700"
              >
                Visualizar em 3D
                <span className="text-indigo-200" aria-hidden="true">
                  &rarr;
                </span>
              </Link>
              {!selectedDome && (
                <button 
                  onClick={() => document.getElementById('contact').scrollIntoView({ behavior: 'smooth' })}
                  className="bg-transparent border-2 border-white text-white px-8 py-4 rounded-lg text-lg font-semibold hover:bg-white hover:text-black transition-colors"
                >
                  Solicitar Orçamento
                </button>
              )}
            </div>
          </motion.div>
        )}
      </AnimatePresence>

      {selectedDome && (
        <button
          onClick={() => setSelectedDome(null)}
          className="absolute top-4 right-4 z-20 bg-white/20 hover:bg-white/30 text-white py-2 px-4 rounded-lg transition-colors"
        >
          Voltar
        </button>
      )}
    </section>
  );
}

export default Hero;
