import React from 'react';
import { motion } from 'framer-motion';

const domeOptions = [
  {
    id: 'dome1',
    name: 'Geodome Classic',
    description: 'Nossa cúpula geodésica clássica, perfeita para espaços amplos',
    size: '6m x 6m',
    modelFile: '/models/geodome-classic.usdz',
    image: '/images/assembly/step1.jpg'
  },
  {
    id: 'dome2',
    name: 'Geodome Compact',
    description: 'Versão compacta ideal para jardins e áreas menores',
    size: '4m x 4m',
    modelFile: '/models/geodome-compact.usdz',
    image: '/images/assembly/step2.jpg'
  }
];

function DomeSection() {
  return (
    <section className="py-16 bg-gray-900">
      <div className="container mx-auto px-4">
        <div className="text-center mb-12">
          <h2 className="text-3xl font-bold text-white mb-4">Nossos Modelos</h2>
          <p className="text-gray-400">Escolha o modelo ideal para seu espaço</p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-8 max-w-4xl mx-auto">
          {domeOptions.map((dome) => (
            <motion.div
              key={dome.id}
              className="bg-gray-800 rounded-lg overflow-hidden shadow-lg"
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
            >
              <div className="relative h-48">
                <img
                  src={dome.image}
                  alt={dome.name}
                  className="w-full h-full object-cover"
                />
                <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-gray-900 to-transparent p-4">
                  <h3 className="text-xl font-semibold text-white">{dome.name}</h3>
                  <p className="text-gray-300 text-sm">{dome.size}</p>
                </div>
              </div>
              <div className="p-4">
                <p className="text-gray-400 text-sm mb-4">{dome.description}</p>
                <div className="flex gap-3">
                  <a
                    href={dome.modelFile}
                    rel="ar"
                    className="flex-1 bg-emerald-600 hover:bg-emerald-700 text-white py-2 px-3 rounded text-center text-sm transition-colors"
                  >
                    Ver em AR
                  </a>
                  <a
                    href="#"
                    className="flex-1 bg-gray-700 hover:bg-gray-600 text-white py-2 px-3 rounded text-center text-sm transition-colors"
                  >
                    Ver em 3D
                  </a>
                </div>
              </div>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
}

export default DomeSection;
