import React, { useState } from 'react';
import { getARUrl } from '../utils/arUtils';

function ModelViewer({ modelUrl }) {
  const [isConverting, setIsConverting] = useState(false);
  const [error, setError] = useState(null);

  const handleARClick = async () => {
    try {
      setIsConverting(true);
      setError(null);
      const arUrl = await getARUrl(modelUrl);
      window.location.href = arUrl;
    } catch (err) {
      setError(err.message);
    } finally {
      setIsConverting(false);
    }
  };

  return (
    <div className="w-full h-[400px] relative">
      <model-viewer
        src={modelUrl}
        camera-controls
        auto-rotate
        style={{ width: '100%', height: '100%', backgroundColor: '#f3f4f6' }}
      >
        <button
          onClick={handleARClick}
          disabled={isConverting}
          className={`absolute bottom-4 left-4 right-4 bg-emerald-600 hover:bg-emerald-700 text-white py-3 px-4 rounded-lg text-center font-medium transition-colors flex items-center justify-center ${
            isConverting ? 'opacity-50 cursor-not-allowed' : ''
          }`}
        >
          {isConverting ? (
            <>
              <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-2"></div>
              Preparando AR...
            </>
          ) : (
            <>
              <svg className="h-5 w-5 mr-2" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 18h.01M8 21h8a2 2 0 002-2V5a2 2 0 00-2-2H8a2 2 0 00-2 2v14a2 2 0 002 2z" />
              </svg>
              Ver em RA
            </>
          )}
        </button>
      </model-viewer>
      {error && (
        <div className="absolute bottom-20 left-4 right-4 bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded">
          {error}
        </div>
      )}
    </div>
  );
}

export default ModelViewer;
