import React, { useState } from 'react';
import { motion } from 'framer-motion';
import ModelViewer from './ModelViewer';

const domeOptions = [
  {
    id: 'dome1',
    name: 'Geodome Nordic',
    description: 'Design escandinavo com painéis de madeira e vidro, perfeito para integração com a natureza',
    size: '6m x 6m',
    modelFile: '/models/geodome-classic.usdz',
    modelFileWeb: '/models/ImageToStl.com_geodome_d15m_4v_woodframe.glb',
    features: [
      'Painéis de madeira natural',
      'Janelas panorâmicas',
      'Porta frontal ampla',
      'Painel solar integrado',
      'Deck de entrada',
      'Sistema de ventilação natural'
    ],
    specifications: {
      'Área': '28m²',
      'Altura': '3.5m',
      'Material': 'Madeira e Vidro',
      'Tempo de Montagem': '2-3 dias',
      'Garantia': '5 anos',
      'Capacidade': '4-6 pessoas'
    }
  }
];

function DomeSection() {
  const [show3DModel, setShow3DModel] = useState(false);

  const handleARClick = async (e, dome) => {
    e.preventDefault();
    try {
      const permissionStatus = await navigator.permissions.query({ name: 'geolocation' });

      if (permissionStatus.state === 'denied') {
        alert('Permissão de localização é necessária para usar o AR');
        return;
      }

      // If we have permission or can request it, proceed to AR view
      window.location.href = dome.modelFile;

      // Try to save location in background if we have permission
      if (permissionStatus.state === 'granted') {
        try {
          const position = await new Promise((resolve, reject) => {
            navigator.geolocation.getCurrentPosition(resolve, reject, {
              enableHighAccuracy: true,
              timeout: 5000,
              maximumAge: 0
            });
          });

          const anchorData = {
            lat: position.coords.latitude,
            lng: position.coords.longitude,
            modelUrl: dome.modelFile,
            timestamp: new Date().toISOString()
          };

          fetch('/api/save-ar-anchor', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify(anchorData),
          }).catch(console.warn);
        } catch (saveError) {
          console.warn('Failed to save location:', saveError);
        }
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Erro ao acessar AR. Por favor, verifique as permissões e tente novamente.');
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-12">
      <div className="container mx-auto px-4">
        <div className="grid gap-8">
          {domeOptions.map((dome) => (
            <motion.div
              key={dome.id}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              className="bg-white rounded-lg shadow-lg overflow-hidden"
            >
              <div className="grid grid-cols-1 md:grid-cols-2 gap-8 p-8">
                <div className="relative aspect-square bg-gray-100 rounded-lg overflow-hidden">
                  {show3DModel ? (
                    <ModelViewer modelUrl={dome.modelFileWeb} />
                  ) : (
                    <div className="w-full h-full flex items-center justify-center text-gray-500">
                      Clique em "Ver em 3D" para visualizar o modelo
                    </div>
                  )}
                </div>

                <div className="space-y-6">
                  <div>
                    <h2 className="text-3xl font-bold text-gray-900">{dome.name}</h2>
                    <p className="mt-4 text-lg text-gray-600">{dome.description}</p>
                  </div>

                  <div className="space-y-4">
                    <div>
                      <h3 className="text-lg font-semibold text-gray-900">Especificações</h3>
                      <dl className="mt-2 grid grid-cols-1 gap-x-6 gap-y-3 sm:grid-cols-2">
                        {Object.entries(dome.specifications).map(([key, value]) => (
                          <div key={key}>
                            <dt className="text-sm font-medium text-gray-500">{key}</dt>
                            <dd className="mt-1 text-sm text-gray-900">{value}</dd>
                          </div>
                        ))}
                      </dl>
                    </div>

                    <div>
                      <h3 className="text-lg font-semibold text-gray-900">Características</h3>
                      <ul className="mt-2 grid grid-cols-1 gap-x-6 gap-y-2 sm:grid-cols-2">
                        {dome.features.map((feature, index) => (
                          <li key={index} className="text-sm text-gray-600 flex items-center">
                            <svg className="h-5 w-5 text-emerald-500 mr-2" viewBox="0 0 20 20" fill="currentColor">
                              <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
                            </svg>
                            {feature}
                          </li>
                        ))}
                      </ul>
                    </div>

                    <div>
                      <h3 className="text-lg font-semibold text-gray-900">Visualizar</h3>
                      <div className="mt-2">
                        <div className="flex gap-4">
                          <a
                            href={dome.modelFile}
                            rel="ar"
                            onClick={(e) => handleARClick(e, dome)}
                            className="flex-1 bg-emerald-600 hover:bg-emerald-700 text-white py-3 px-4 rounded-lg text-center font-medium transition-colors flex items-center justify-center"
                          >
                            <svg className="h-5 w-5 mr-2" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 18h.01M8 21h8a2 2 0 002-2V5a2 2 0 00-2-2H8a2 2 0 00-2 2v14a2 2 0 002 2z" />
                            </svg>
                            Ver em RA
                          </a>
                          <button
                            type="button"
                            onClick={() => setShow3DModel(!show3DModel)}
                            className="flex-1 bg-gray-100 hover:bg-gray-200 text-gray-800 py-3 px-4 rounded-lg text-center font-medium transition-colors flex items-center justify-center"
                          >
                            <svg className="h-5 w-5 mr-2" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
                            </svg>
                            {show3DModel ? 'Ocultar 3D' : 'Ver em 3D'}
                          </button>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </motion.div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default DomeSection;
