import React, { useEffect, useRef } from 'react';

function ModelViewer({ modelUrl }) {
  const modelViewerRef = useRef(null);

  useEffect(() => {
    // Cleanup function to properly remove event listeners
    return () => {
      if (modelViewerRef.current) {
        const element = modelViewerRef.current;
        // Remove all event listeners
        const clone = element.cloneNode(true);
        element.parentNode?.replaceChild(clone, element);
      }
    };
  }, []);

  const handleARClick = (e) => {
    e.preventDefault(); // Prevent default behavior

    // For iOS, use USDZ file
    if (/iPhone|iPad|iPod/.test(navigator.userAgent)) {
      // Clean up model-viewer before navigating
      if (modelViewerRef.current) {
        const element = modelViewerRef.current;
        const clone = element.cloneNode(true);
        element.parentNode?.replaceChild(clone, element);
      }

      const usdzUrl = modelUrl.replace(/\.glb$/, '.usdz');
      window.location.href = usdzUrl;
      return;
    }

    // For other devices, use the GLB with WebXR
    const handlerUrl = `${window.location.origin}/ar-handler.html?model=${encodeURIComponent(modelUrl)}`;
    window.location.href = handlerUrl;
  };

  return (
    <div className="w-full h-[400px] relative">
      <model-viewer
        ref={modelViewerRef}
        src={modelUrl}
        ios-src={modelUrl.replace(/\.glb$/, '.usdz')}
        camera-controls
        auto-rotate
        ar
        ar-modes="webxr scene-viewer quick-look"
        style={{ width: '100%', height: '100%', backgroundColor: '#f3f4f6' }}
      >
        <button
          onClick={handleARClick}
          className="absolute bottom-4 left-4 right-4 bg-emerald-600 hover:bg-emerald-700 text-white py-3 px-4 rounded-lg text-center font-medium transition-colors flex items-center justify-center"
        >
          <svg className="h-5 w-5 mr-2" viewBox="0 0 24 24" fill="none" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 18h.01M8 21h8a2 2 0 002-2V5a2 2 0 00-2-2H8a2 2 0 00-2 2v14a2 2 0 002 2z" />
          </svg>
          Ver em AR
        </button>
      </model-viewer>
    </div>
  );
}

export default ModelViewer;
