import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import ModelViewer from './ModelViewer';
import QRCode from 'qrcode.react';

const domeOptions = [
  {
    id: 'dome1',
    name: 'Geodome Nordic',
    description: 'Design escandinavo com painéis de madeira e vidro, perfeito para integração com a natureza',
    size: '6m x 6m',
    modelFile: '/models/geodome-classic.usdz',
    modelFileWeb: '/models/ImageToStl.com_geodome_d15m_4v_woodframe.glb',
    image: '/images/domes/snowdon-dome.jpg',
    features: [
      'Painéis de madeira natural',
      'Janelas panorâmicas',
      'Porta frontal ampla',
      'Painel solar integrado',
      'Deck de entrada',
      'Sistema de ventilação natural'
    ],
    specifications: {
      'Área': '28m²',
      'Altura': '3.5m',
      'Material': 'Madeira e Vidro',
      'Tempo de Montagem': '2-3 dias',
      'Garantia': '5 anos',
      'Capacidade': '4-6 pessoas'
    }
  }
];

function DomeSection() {
  const [show3DModel, setShow3DModel] = useState(false);
  const [arCaptures, setArCaptures] = useState([]);
  const [selectedCapture, setSelectedCapture] = useState(null);
  const [showShareModal, setShowShareModal] = useState(false);
  const [showARPrompt, setShowARPrompt] = useState(false);
  const [shareUrl, setShareUrl] = useState('');
  const [currentDome, setCurrentDome] = useState(null);

  // Listen for AR screenshots
  useEffect(() => {
    const handleVisibilityChange = () => {
      if (!document.hidden) {
        // User returned from AR view, check for new photos
        checkForNewARPhotos();
      }
    };

    document.addEventListener('visibilitychange', handleVisibilityChange);
    return () => document.removeEventListener('visibilitychange', handleVisibilityChange);
  }, []);

  const checkForNewARPhotos = async () => {
    try {
      // Request permission to access photos
      const permission = await navigator.permissions.query({ name: 'camera' });
      if (permission.state === 'granted') {
        // Check for new photos in the last minute
        const newPhotos = await getRecentPhotos();
        if (newPhotos.length > 0) {
          setArCaptures(prev => [...newPhotos, ...prev]);
        }
      }
    } catch (error) {
      console.error('Error checking for AR photos:', error);
    }
  };

  const handleShareCapture = (capture) => {
    setSelectedCapture(capture);
    setShowShareModal(true);
  };

  const handleARClick = (dome) => {
    setCurrentDome(dome);
    setShowARPrompt(true);
  };

  const startARWithLocation = async () => {
    try {
      const position = await new Promise((resolve, reject) => {
        navigator.geolocation.getCurrentPosition(resolve, reject);
      });

      // Create a URL with location data
      const baseUrl = window.location.origin;
      const arUrl = new URL(currentDome.modelFile, baseUrl);
      arUrl.searchParams.set('lat', position.coords.latitude);
      arUrl.searchParams.set('lng', position.coords.longitude);
      arUrl.searchParams.set('share', 'true');

      // Set the share URL and show sharing modal
      setShareUrl(arUrl.toString());
      setShowARPrompt(false);
      setShowShareModal(true);
    } catch (error) {
      console.error('Error getting location:', error);
      // If location fails, just open AR without location
      window.location.href = currentDome.modelFile;
    }
  };

  const startARWithoutLocation = () => {
    window.location.href = currentDome.modelFile;
  };

  const openARWithSharedLocation = () => {
    if (shareUrl) {
      window.location.href = shareUrl;
    }
  };

  const handle3DClick = (dome) => {
    setShow3DModel(true);
  };

  const handleCloseModals = () => {
    setShowARPrompt(false);
    setShowShareModal(false);
    setShareUrl('');
  };

  return (
    <div className="min-h-screen bg-gray-50 py-12">
      <div className="container mx-auto px-4">
        <div className="grid gap-8">
          {domeOptions.map((dome) => (
            <motion.div
              key={dome.id}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              className="bg-white rounded-lg shadow-lg overflow-hidden"
            >
              {/* Main content */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-8 p-8">
                <div className="relative aspect-square bg-gray-100 rounded-lg overflow-hidden">
                  {show3DModel ? (
                    <ModelViewer modelUrl={dome.modelFileWeb} />
                  ) : (
                    <img
                      src={dome.image}
                      alt={dome.name}
                      className="w-full h-full object-cover cursor-pointer"
                      onClick={() => handle3DClick(dome)}
                    />
                  )}
                </div>

                <div className="space-y-6">
                  <div>
                    <h2 className="text-2xl font-bold text-gray-900">{dome.name}</h2>
                    <p className="mt-2 text-gray-600">{dome.description}</p>
                    <p className="mt-1 text-sm text-gray-500">{dome.size}</p>
                  </div>

                  <div>
                    <h3 className="text-lg font-semibold text-gray-900">Visualizar</h3>
                    <div className="mt-2 space-y-4">
                      <div className="flex gap-4">
                        <a
                          href={dome.modelFile}
                          rel="ar"
                          className="flex-1 bg-emerald-600 hover:bg-emerald-700 text-white py-3 px-4 rounded-lg text-center font-medium transition-colors flex items-center justify-center"
                        >
                          <svg className="h-5 w-5 mr-2" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 18h.01M8 21h8a2 2 0 002-2V5a2 2 0 00-2-2H8a2 2 0 00-2 2v14a2 2 0 002 2z" />
                          </svg>
                          Ver em RA
                        </a>
                        <button
                          onClick={() => handleARClick(dome)}
                          className="flex-1 bg-emerald-600 hover:bg-emerald-700 text-white py-3 px-4 rounded-lg text-center font-medium transition-colors flex items-center justify-center"
                        >
                          <svg className="h-5 w-5 mr-2" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 18h.01M8 21h8a2 2 0 002-2V5a2 2 0 00-2-2H8a2 2 0 00-2 2v14a2 2 0 002 2z" />
                          </svg>
                          Ver em RA com Localização
                        </button>
                        <button
                          onClick={() => setShow3DModel(!show3DModel)}
                          className="flex-1 bg-gray-100 hover:bg-gray-200 text-gray-800 py-3 px-4 rounded-lg text-center font-medium transition-colors flex items-center justify-center"
                        >
                          <svg className="h-5 w-5 mr-2" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
                          </svg>
                          {show3DModel ? 'Ocultar 3D' : 'Ver em 3D'}
                        </button>
                      </div>

                      {/* Instructions for AR capture */}
                      <div className="bg-gray-50 p-4 rounded-lg">
                        <h4 className="font-medium text-gray-900 mb-2">Como compartilhar sua visualização em RA:</h4>
                        <ol className="text-sm text-gray-600 space-y-2">
                          <li>1. Clique em "Ver em RA" e posicione o modelo</li>
                          <li>2. Tire uma foto usando o botão de captura do seu dispositivo</li>
                          <li>3. Volte para esta página para compartilhar</li>
                        </ol>
                      </div>

                      {/* AR Captures Gallery */}
                      {arCaptures.length > 0 && (
                        <div className="mt-4">
                          <h4 className="font-medium text-gray-900 mb-2">Suas capturas recentes:</h4>
                          <div className="grid grid-cols-3 gap-2">
                            {arCaptures.map((capture, index) => (
                              <div key={index} className="relative aspect-square bg-gray-100 rounded-lg overflow-hidden">
                                <img
                                  src={capture.url}
                                  alt={`AR Capture ${index + 1}`}
                                  className="w-full h-full object-cover"
                                />
                                <button
                                  onClick={() => handleShareCapture(capture)}
                                  className="absolute bottom-2 right-2 bg-white p-2 rounded-full shadow-lg"
                                >
                                  <svg className="h-4 w-4 text-gray-600" viewBox="0 0 20 20" fill="currentColor">
                                    <path d="M15 8a3 3 0 10-2.977-2.63l-4.94 2.47a3 3 0 100 4.319l4.94 2.47a3 3 0 10.895-1.789l-4.94-2.47a3.027 3.027 0 000-.74l4.94-2.47C13.456 7.68 14.19 8 15 8z" />
                                  </svg>
                                </button>
                              </div>
                            ))}
                          </div>
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            </motion.div>
          ))}
        </div>
      </div>

      {/* AR Prompt Modal */}
      {showARPrompt && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg max-w-sm w-full mx-4">
            <h3 className="text-lg font-semibold mb-4">Visualizar em AR</h3>
            <p className="text-sm text-gray-600 mb-6">
              Deseja compartilhar sua localização para permitir que outras pessoas vejam este objeto no mesmo lugar?
            </p>
            <div className="space-y-2">
              <button
                onClick={startARWithLocation}
                className="w-full bg-emerald-600 hover:bg-emerald-700 text-white py-2 px-4 rounded-lg text-center font-medium transition-colors"
              >
                Sim, compartilhar localização
              </button>
              <button
                onClick={startARWithoutLocation}
                className="w-full bg-gray-100 hover:bg-gray-200 text-gray-800 py-2 px-4 rounded-lg text-center font-medium transition-colors"
              >
                Não, apenas ver em AR
              </button>
              <button
                onClick={handleCloseModals}
                className="w-full text-gray-500 py-2 px-4 text-center font-medium"
              >
                Cancelar
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Share Modal */}
      {showShareModal && (selectedCapture || shareUrl) && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg max-w-sm w-full mx-4">
            <h3 className="text-lg font-semibold mb-4">Compartilhar Visualização AR</h3>
            {selectedCapture && (
              <div className="aspect-square bg-gray-100 rounded-lg overflow-hidden mb-4">
                <img
                  src={selectedCapture.url}
                  alt="AR Capture"
                  className="w-full h-full object-cover"
                />
              </div>
            )}
            <div className="flex justify-center mb-4">
              <QRCode value={selectedCapture ? selectedCapture.shareUrl : shareUrl} size={150} />
            </div>
            <div className="space-y-2">
              <button
                onClick={() => {
                  navigator.clipboard.writeText(selectedCapture ? selectedCapture.shareUrl : shareUrl);
                  alert('Link copiado!');
                }}
                className="w-full bg-emerald-600 hover:bg-emerald-700 text-white py-2 px-4 rounded-lg text-center font-medium transition-colors"
              >
                Copiar Link
              </button>
              <button
                onClick={() => {
                  if (selectedCapture) {
                    window.location.href = selectedCapture.shareUrl;
                  } else {
                    openARWithSharedLocation();
                  }
                }}
                className="w-full bg-emerald-600 hover:bg-emerald-700 text-white py-2 px-4 rounded-lg text-center font-medium transition-colors"
              >
                Abrir em AR
              </button>
              <button
                onClick={handleCloseModals}
                className="w-full bg-gray-100 hover:bg-gray-200 text-gray-800 py-2 px-4 rounded-lg text-center font-medium transition-colors"
              >
                Fechar
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default DomeSection;
