import React from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';

function Hero() {
  return (
    <section className="relative h-screen flex items-center overflow-hidden">
      <div className="absolute inset-0 z-0">
        <img
          src="/images/hero/hero-main.jpg"
          alt="Geodome in nature"
          className="w-full h-full object-cover"
          onError={(e) => {
            e.target.onerror = null;
            e.target.src = 'https://via.placeholder.com/1920x1080?text=Geodome+Flat+Pack';
          }}
        />
        <div className="absolute inset-0 bg-gradient-to-r from-black/70 to-black/50"></div>
      </div>

      <div className="relative container mx-auto px-4 z-10">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          className="text-white max-w-3xl"
        >
          <h1 className="text-6xl font-bold mb-6">
            Geodome Flat Pack
          </h1>
          <p className="text-xl mb-8">
            Design escandinavo, montagem simples e conforto premium.
            Seu refúgio na natureza com design minimalista.
          </p>
          <div className="flex flex-col sm:flex-row gap-4">
            <Link
              to="/3d-view"
              className="inline-block rounded-lg bg-indigo-600 px-8 py-4 text-base font-semibold leading-7 text-white shadow-sm ring-1 ring-indigo-600 hover:bg-indigo-700 hover:ring-indigo-700"
            >
              Visualizar em 3D
              <span className="text-indigo-200 ml-2" aria-hidden="true">
                &rarr;
              </span>
            </Link>
            <button 
              onClick={() => document.getElementById('contact').scrollIntoView({ behavior: 'smooth' })}
              className="bg-transparent border-2 border-white text-white px-8 py-4 rounded-lg text-lg font-semibold hover:bg-white hover:text-black transition-colors"
            >
              Solicitar Orçamento
            </button>
          </div>
        </motion.div>
      </div>
    </section>
  );
}

export default Hero;
