import React, { Suspense, useState, useRef } from 'react';
import { Canvas, useFrame, useThree } from '@react-three/fiber';
import { Environment, OrbitControls, useGLTF, Plane, PerspectiveCamera, Text } from '@react-three/drei';
import * as THREE from 'three';

function DetailedTent({ position, rotation = 0, color, onClick }) {
  const [hovered, setHovered] = useState(false);
  const tentRef = useRef();

  return (
    <group 
      position={position} 
      rotation={[0, rotation, 0]}
      onClick={onClick}
      onPointerOver={() => setHovered(true)}
      onPointerOut={() => setHovered(false)}
      ref={tentRef}
    >
      {/* Main tent body */}
      <mesh castShadow receiveShadow>
        <cylinderBufferGeometry args={[1.2, 1.5, 1.8, 8]} />
        <meshStandardMaterial 
          color={color} 
          roughness={0.7}
          metalness={0.1}
          emissive={hovered ? new THREE.Color(color).multiplyScalar(0.2) : 'black'}
        />
      </mesh>

      {/* Tent top */}
      <mesh castShadow receiveShadow position={[0, 0.9, 0]}>
        <coneBufferGeometry args={[1.2, 1, 8]} />
        <meshStandardMaterial 
          color={color} 
          roughness={0.7}
          metalness={0.1}
        />
      </mesh>

      {/* Tent entrance */}
      <group position={[0, -0.5, 1.2]}>
        {/* Door frame */}
        <mesh castShadow receiveShadow>
          <boxBufferGeometry args={[1, 1.4, 0.1]} />
          <meshStandardMaterial color={color} />
        </mesh>

        {/* Door flap */}
        <mesh 
          castShadow 
          receiveShadow
          position={[0.4, 0, 0.05]}
          rotation={[0, -0.3, 0]}
        >
          <planeBufferGeometry args={[0.8, 1.2]} />
          <meshStandardMaterial 
            color={color} 
            side={THREE.DoubleSide}
          />
        </mesh>
      </group>

      {/* Ground sheet */}
      <mesh 
        receiveShadow
        rotation={[-Math.PI / 2, 0, 0]} 
        position={[0, -0.89, 0]}
      >
        <circleBufferGeometry args={[1.5, 32]} />
        <meshStandardMaterial color="#2c2c2c" />
      </mesh>

      {hovered && (
        <Text
          position={[0, 2, 0]}
          fontSize={0.3}
          color="white"
          anchorX="center"
          anchorY="middle"
        >
          Click to enter
        </Text>
      )}
    </group>
  );
}

function TentInterior({ position, onExit }) {
  const { camera } = useThree();

  React.useEffect(() => {
    const originalPosition = camera.position.clone();
    camera.position.set(position[0], position[1] + 0.5, position[2]);

    return () => {
      camera.position.copy(originalPosition);
    };
  }, [camera, position]);

  return (
    <group position={position}>
      <mesh position={[0, 1, -1]}>
        <Text
          fontSize={0.2}
          color="white"
          anchorX="center"
          anchorY="middle"
          onClick={onExit}
        >
          Exit Tent
        </Text>
      </mesh>

      {/* Interior details */}
      <mesh position={[0.5, 0, -0.5]} castShadow>
        <boxBufferGeometry args={[0.4, 0.2, 0.6]} />
        <meshStandardMaterial color="#8B4513" />
      </mesh>

      <mesh position={[-0.5, 0.1, -0.7]} castShadow>
        <cylinderBufferGeometry args={[0.2, 0.2, 0.1, 16]} />
        <meshStandardMaterial color="#A0522D" />
      </mesh>

      <pointLight position={[0, 1.5, 0]} intensity={0.5} />
    </group>
  );
}

function TallTree({ position, scale = 1 }) {
  const height = 8 * scale;
  const width = 2 * scale;

  return (
    <group position={position}>
      {/* Tree trunk */}
      <mesh castShadow position={[0, height/2, 0]}>
        <cylinderGeometry args={[0.3 * scale, 0.4 * scale, height, 8]} />
        <meshStandardMaterial color="#4a3728" />
      </mesh>
      {/* Tree foliage layers */}
      {[...Array(4)].map((_, i) => (
        <mesh 
          key={i} 
          castShadow 
          position={[
            0, 
            height - (i * 1.5 * scale), 
            0
          ]}
        >
          <sphereGeometry args={[width - (i * 0.3), 16, 16]} />
          <meshStandardMaterial color={i % 2 ? "#2d4a1d" : "#1a472a"} />
        </mesh>
      ))}
    </group>
  );
}

function GrassField() {
  return (
    <Plane
      receiveShadow
      rotation={[-Math.PI / 2, 0, 0]}
      position={[0, -0.1, 0]}
      args={[100, 100]}
    >
      <meshStandardMaterial
        color="#90a959"
        roughness={1}
        metalness={0}
      />
    </Plane>
  );
}

function BackgroundHills() {
  return (
    <group position={[0, -5, -30]}>
      <mesh receiveShadow>
        <cylinderGeometry args={[40, 40, 20, 32, 1, true, 0, Math.PI]} />
        <meshStandardMaterial color="#2d4a1d" side={2} />
      </mesh>
    </group>
  );
}

function CampingScene() {
  const [activeTent, setActiveTent] = useState(null);

  const tents = [
    { position: [-8, 0, 5], rotation: Math.PI * 0.2, color: "#4299e1" },
    { position: [-5, 0, 8], rotation: Math.PI * -0.1, color: "#ecc94b" },
    { position: [6, 0, 7], rotation: Math.PI * 0.15, color: "#48bb78" }
  ];

  return (
    <group>
      <GrassField />
      <BackgroundHills />

      {/* Background tall trees */}
      {[...Array(12)].map((_, i) => (
        <TallTree
          key={i}
          position={[
            -25 + (i * 4) + (Math.random() * 2),
            0,
            -15 + (Math.random() * 3)
          ]}
          scale={1 + Math.random() * 0.5}
        />
      ))}

      {/* Interactive tents */}
      {tents.map((tent, i) => (
        <DetailedTent
          key={i}
          {...tent}
          onClick={() => setActiveTent(i)}
        />
      ))}

      {activeTent !== null && (
        <TentInterior
          position={tents[activeTent].position}
          onExit={() => setActiveTent(null)}
        />
      )}

      {/* Enhanced gazebo */}
      <group position={[0, 0, 8]}>
        <mesh position={[0, 1.8, 0]} castShadow receiveShadow>
          <boxBufferGeometry args={[3.2, 0.2, 3.2]} />
          <meshStandardMaterial color="#795548" roughness={0.9} />
        </mesh>

        {/* Roof top */}
        <mesh position={[0, 2.2, 0]} castShadow>
          <coneBufferGeometry args={[2.3, 0.8, 4]} />
          <meshStandardMaterial color="#4a3528" roughness={0.8} />
        </mesh>

        {/* Posts with detail */}
        {[[-1.4, -1.4], [1.4, -1.4], [-1.4, 1.4], [1.4, 1.4]].map(([x, z], i) => (
          <group key={i} position={[x, 0, z]}>
            <mesh position={[0, 0.9, 0]} castShadow>
              <cylinderBufferGeometry args={[0.12, 0.12, 1.8, 8]} />
              <meshStandardMaterial color="#5d4037" roughness={0.7} />
            </mesh>
            <mesh position={[0, 0, 0]} receiveShadow>
              <cylinderBufferGeometry args={[0.2, 0.2, 0.1, 8]} />
              <meshStandardMaterial color="#3e2723" roughness={0.6} />
            </mesh>
          </group>
        ))}
      </group>
    </group>
  );
}

function Model() {
  const { scene } = useGLTF('/models/zomepod.glb');

  return (
    <primitive 
      object={scene} 
      scale={1} 
      position={[0, 0, 0]}
      castShadow
      receiveShadow
    />
  );
}

