import React, { useState } from 'react';
import { motion } from 'framer-motion';
import ModelViewer from './ModelViewer';
import QRCode from 'qrcode.react';

const domeOptions = [
  {
    id: 'dome1',
    name: 'Geodome Nordic',
    description: 'Design escandinavo com painéis de madeira e vidro, perfeito para integração com a natureza',
    size: '6m x 6m',
    modelFile: '/models/geodome-classic.usdz',
    modelFileWeb: '/models/ImageToStl.com_geodome_d15m_4v_woodframe.glb',
    image: '/images/domes/snowdon-dome.jpg',
    features: [
      'Painéis de madeira natural',
      'Janelas panorâmicas',
      'Porta frontal ampla',
      'Painel solar integrado',
      'Deck de entrada',
      'Sistema de ventilação natural'
    ],
    specifications: {
      'Área': '28m²',
      'Altura': '3.5m',
      'Material': 'Madeira e Vidro',
      'Tempo de Montagem': '2-3 dias',
      'Garantia': '5 anos',
      'Capacidade': '4-6 pessoas'
    }
  }
];

function DomeSection() {
  const [show3DModel, setShow3DModel] = useState(false);
  const [showShareModal, setShowShareModal] = useState(false);
  const [shareUrl, setShareUrl] = useState('');

  const handleARClick = (dome) => {
    // Create URL with model info
    const baseUrl = window.location.origin;
    const arUrl = new URL(dome.modelFile, baseUrl);
    window.location.href = arUrl.toString();
  };

  const handle3DClick = () => {
    setShow3DModel(!show3DModel);
  };

  return (
    <div className="min-h-screen bg-gray-50 py-12">
      <div className="container mx-auto px-4">
        <div className="grid gap-8">
          {domeOptions.map((dome) => (
            <motion.div
              key={dome.id}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              className="bg-white rounded-lg shadow-lg overflow-hidden"
            >
              <div className="grid grid-cols-1 md:grid-cols-2 gap-8 p-8">
                <div className="relative aspect-square bg-gray-100 rounded-lg overflow-hidden">
                  {show3DModel ? (
                    <ModelViewer modelUrl={dome.modelFileWeb} />
                  ) : (
                    <img
                      src={dome.image}
                      alt={dome.name}
                      className="w-full h-full object-cover cursor-pointer"
                      onClick={handle3DClick}
                    />
                  )}
                </div>

                <div className="space-y-6">
                  <div>
                    <h2 className="text-2xl font-bold text-gray-900">{dome.name}</h2>
                    <p className="mt-2 text-gray-600">{dome.description}</p>
                    <p className="mt-1 text-sm text-gray-500">{dome.size}</p>
                  </div>

                  <div>
                    <h3 className="text-lg font-semibold text-gray-900">Visualizar</h3>
                    <div className="mt-2 space-y-4">
                      <div className="flex gap-4">
                        <button
                          onClick={() => handleARClick(dome)}
                          className="flex-1 bg-emerald-600 hover:bg-emerald-700 text-white py-3 px-4 rounded-lg text-center font-medium transition-colors flex items-center justify-center"
                        >
                          <svg className="h-5 w-5 mr-2" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 18h.01M8 21h8a2 2 0 002-2V5a2 2 0 00-2-2H8a2 2 0 00-2 2v14a2 2 0 002 2z" />
                          </svg>
                          Ver em RA
                        </button>
                        <button
                          onClick={handle3DClick}
                          className="flex-1 bg-gray-100 hover:bg-gray-200 text-gray-800 py-3 px-4 rounded-lg text-center font-medium transition-colors flex items-center justify-center"
                        >
                          <svg className="h-5 w-5 mr-2" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
                          </svg>
                          {show3DModel ? 'Ocultar 3D' : 'Ver em 3D'}
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </motion.div>
          ))}
        </div>
      </div>

      {/* Share Modal */}
      {showShareModal && shareUrl && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg max-w-sm w-full mx-4">
            <h3 className="text-lg font-semibold mb-4">Compartilhar Visualização AR</h3>
            <div className="flex justify-center mb-4">
              <QRCode value={shareUrl} size={200} />
            </div>
            <p className="text-sm text-gray-600 mb-4 text-center">
              Escaneie este QR code ou compartilhe o link para ver o objeto AR na mesma posição
            </p>
            <div className="space-y-2">
              <button
                onClick={() => {
                  navigator.clipboard.writeText(shareUrl);
                  alert('Link copiado!');
                }}
                className="w-full bg-emerald-600 hover:bg-emerald-700 text-white py-2 px-4 rounded-lg text-center font-medium transition-colors"
              >
                Copiar Link
              </button>
              <button
                onClick={() => setShowShareModal(false)}
                className="w-full bg-gray-100 hover:bg-gray-200 text-gray-800 py-2 px-4 rounded-lg text-center font-medium transition-colors"
              >
                Fechar
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default DomeSection;
